FilePreview.codeSample:
const FilePreviewSample = () => {
  const [isLoading, setIsLoading] = useState(true);
  const boardData = useGetBoardData();
  const [fileColumns, setFileColumns] = useState([]);

  useEffect(() => {
    if (boardData.boards) {
      setIsLoading(false);
      setFileColumns(
        boardData.boards[0].columns.filter((x) => x.type === "file")
      );
    }
  }, [boardData]);


  return (
    <div>
      {isLoading ? (
        <Loader size={16} />
      ) : fileColumns.length > 0 ? (
        <RenderItems
          itemsData={boardData.boards[0].items_page.items}
          actionButtonContent="Add or preview File"
          action={(item) => {
            const fileColumnId = fileColumns[0].id;
            const fileColumnValue = JSON.parse(
              item.column_values.filter((x) => x.id === fileColumns[0].id)[0]
                .value
            );
            const assetId = fileColumnValue?.files[0]?.assetId ?? null;
            const boardId = boardData.boards[0].id;
            console.log({ item, assetId, fileColumnId });
            if (!assetId) {
              monday.execute("notice", {
                message: "No files uploaded. Uploading now...",
              });
              monday.execute("triggerFilesUpload", {
                boardId,
                itemId: item.id,
                columnId: fileColumnId,
              });
            } else {
              monday.execute("openFilesDialog", {
                boardId,
                itemId: item.id,
                columnId: fileColumnId,
                assetId,
              });
            }
          }}
        />
      ) : (
        <Button onClick={handleAddFileColumn}>Add file column</Button>
      )}
    </div>
  );
};

DeleteItem.codeSample:
const monday = mondaySdk();

const DeleteItem = () => {
  const boardContext = useBoardContext();
  const { items, boardName, updateItems } = boardContext.state;

  const deleteItem = (item) => {
    monday
      .api(deleteItemConstants.deleteItemAndGetUpdatedBoardItemsQuery, {
        variables: { itemId: +item.id },
      })
      .then((res) => {
        updateItems({
          items: res.data.delete_item.board.items_page.items,
          boardName,
          updateItems,
        });
      });
  };

  return (
    <div className="delete-item-container feature-container">
      <RenderItems
        itemsData={items}
        actionButtonContent="Delete me"
        action={(item) => {
          monday
            .execute("confirm", {
              message: `Are you sure you want to delete the item ${item.name} (This item will be actually deleted from you board)?`,
              confirmButton: "Delete",
              cancelButton: "Cancel",
              excludeCancelButton: false,
            })
            .then((res) => {
              res.data.confirm &&
                monday.execute("notice", {
                  message: "Item has been deleted successfully",
                  type: "success",
                  timeout: 10000,
                }) &&
                deleteItem(item);
            });
        }}
      />

    </div>
  );
};

StorageApi.codeSample:
const monday = mondaySdk();

const StorageApi = () => {
  const { items } = useBoardContext().state;
  const [favouriteItems, setFavouriteItems] = useState([]);

  useEffect(() => {
    monday.storage.instance.getItem("FAVOURITE_ITEMS").then((res) => {
      if (!res.data?.value || !res.data?.success) {
        monday.storage.instance.setItem("FAVOURITE_ITEMS", JSON.stringify([]));
        return;
      } else {
        setFavouriteItems(JSON.parse(res.data?.value));
      }
    });

    monday.execute("notice", {
      message: "Click on the heart icon to mark item as favourite or to remove one",
      type: "info",
      timeout: 10000,
    });
  }, []);

  const changeItemFavouriteState = (itemId, desiredFavouriteState) => {
    let updatedFavourites = [...favouriteItems];
    if (desiredFavouriteState) {
      if (!favouriteItems.includes(+itemId)) {
        updatedFavourites.push(+itemId);
      }
    } else {
      updatedFavourites = updatedFavourites.filter((favouriteItemId) => {
        return favouriteItemId !== +itemId;
      });
    }
    monday.storage.instance.setItem("FAVOURITE_ITEMS", JSON.stringify(updatedFavourites)).then((res) => {
      if (res.data.success) {
        setFavouriteItems(updatedFavourites);
      }
    });
  };

  return (
    <div className="storage-api-container feature-container">
      <div className="working-with-the-board-items playground">
        <h3 className="playground-header">Playground</h3>
        {items.map((item) => {
          const isFavourite = favouriteItems.includes(+item.id);
          return (
            <Item
              key={item.id}
              item={item}
              action={() => changeItemFavouriteState(item.id, !isFavourite)}
              isFavourite={isFavourite}
              backgroundColor={randomColorGenerator()}
            />
          );
        })}
      </div>
    </div>
  );
};

